package com.example.furnitureshop.db

import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

suspend fun rePopulateDb(database: FurnitureDatabase?) {
    database?.let { db ->
        withContext(Dispatchers.IO) {
            val furnitureDao: FurnitureDao = db.furnitureDao()

            val furnitureOne = FurnitureTable(
                    namaProduk = "Kursi",
                    deskripsi = "Sebuah kursi yang nyaman",
                    harga = 100000,
                    made = "Kresna",
                    kategori = "Kursi",
                    image = "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c6/Set_of_fourteen_side_chairs_MET_DP110780.jpg/220px-Set_of_fourteen_side_chairs_MET_DP110780.jpg"
            )
            val furnitureTwo = FurnitureTable(
                    namaProduk = "Meja",
                    deskripsi = "Sebuah Meja yang kokoh",
                    harga = 150000,
                    made = "Kresna",
                    kategori = "Meja",
                    image = "data:image/jpeg;base64,"
            )
            val furnitureThree = FurnitureTable(
                    namaProduk = "Sofa",
                    deskripsi = "Sebuah sofa yang nyaman",
                    harga = 200000,
                    made = "Kresna",
                    kategori = "Sofa",
                    image = "data:image/jpeg;base64,"
            )
            val furnitureFour = FurnitureTable(
                    namaProduk = "Lemari",
                    deskripsi = "Sebuah lemari serbaguna",
                    harga = 60000,
                    made = "Kresna",
                    kategori = "Lemari",
                    image = "data:image/jpeg;base64,"
            )

            furnitureDao.insert(furnitureOne, furnitureTwo, furnitureThree, furnitureFour)

        }
    }
}